<?phprequire_once('config.php');// Handle AJAX request (start)if( isset($_POST['name']) ){ $data = $_POST['name'];  $sql = "SELECT `id`, `empName`, `empEmail`, `empPhone`, `empId`, `empDesign`, `empSalary`, `empDoj`, `empCreated`, `empUpdated` FROM `employees` WHERE `id`= $data";$result = $conn->query($sql);if ($result->num_rows > 0) { // how many rows fetching are there    // output data of each row	$data = array() ;    while($row = $result->fetch_assoc()) {		 $data[] = $row;    }} else {    echo "0 results";}echo json_encode($data);$conn->close();}// Handle AJAX request (end)?>